@@ -0,0 +1,26 @@
+import Image from 'next/image'
+import { ComponentProps } from 'react'
+
+interface SEOImageProps extends Omit<ComponentProps<typeof Image>, 'alt'> {
+  alt: string
+  title?: string
+  loading?: 'lazy' | 'eager'
+}
+
+export default function SEOImage({ 
+  alt, 
+  title, 
+  loading = 'lazy',
+  ...props 
+}: SEOImageProps) {
+  return (
+    <Image
+      {...props}
+      alt={alt}
+      title={title || alt}
+      loading={loading}
+      placeholder="blur"
+      blurDataURL="data:image/jpeg;base64,"
+    />
+  )
+}

