import "@/styles/globals.css";
import type { Metadata, Viewport } from "next";
import { getMessages } from "next-intl/server";
import { NextIntlClientProvider } from "next-intl";
import { setRequestLocale } from 'next-intl/server';
import type { ReactNode } from 'react';

export const viewport: Viewport = {
	width: "device-width",
	initialScale: 1,
	themeColor: "#ffffff"
};

// Base metadata - can be enhanced with generateMetadata for dynamic content
export const metadata: Metadata = {
	title: {
		default: "AICERA - Smarter Tech. Sharper Minds",
		template: "%s | AICERA"
	},
	description: "AICERA website by Samanyu B Rao - Innovative technology solutions and sharp minds working together",
	keywords: ["AICERA", "technology", "innovation", "AI", "software development"],
	authors: [{ name: "Samanyu B Rao" }],
	creator: "Samanyu B Rao",
	robots: {
		index: true,
		follow: true,
		googleBot: {
			index: true,
			follow: true,
			'max-video-preview': -1,
			'max-image-preview': 'large',
			'max-snippet': -1,
		},
	},
	openGraph: {
		title: "AICERA - Smarter Tech. Sharper Minds",
		description: "AICERA website by Samanyu B Rao - Innovative technology solutions and sharp minds working together",
		type: "website",
		siteName: "AICERA",
		locale: "en_US",
		alternateLocale: ["nl_NL"],
	},
	twitter: {
		card: "summary_large_image",
		title: "AICERA - Smarter Tech. Sharper Minds",
		description: "AICERA website by Samanyu B Rao - Innovative technology solutions and sharp minds working together",
	},
	alternates: {
		canonical: "/",
		languages: {
			'en': '/en',
			'nl': '/nl',
		},
	},
};

// Define supported locales in a constant for better maintainability
const SUPPORTED_LOCALES = ['en', 'nl'] as const;

export async function generateStaticParams() {
	return SUPPORTED_LOCALES.map(locale => ({ locale }));
}

type Props = {
	children: ReactNode;
	params: Promise<{ locale: string }>;
};

// Type guard for locale validation
function isValidLocale(locale: string): locale is typeof SUPPORTED_LOCALES[number] {
	return SUPPORTED_LOCALES.includes(locale as any);
}

export default async function RootLayout({ children, params }: Props) {
	const resolvedParams = await params;
	const locale = resolvedParams.locale;

	// Enhanced validation with better error messages
	if (typeof locale !== 'string') {
		throw new Error(`Invalid locale parameter: expected string, got ${typeof locale}`);
	}

	if (!isValidLocale(locale)) {
		throw new Error(`Unsupported locale: ${locale}. Supported locales: ${SUPPORTED_LOCALES.join(', ')}`);
	}

	setRequestLocale(locale);

	// Add error handling for message loading
	let messages;
	try {
		messages = await getMessages({ locale });
	} catch (error) {
		console.error(`Failed to load messages for locale ${locale}:`, error);
		// Fallback to empty messages object to prevent complete failure
		messages = {};
	}

	return (
		<html lang={locale} dir="ltr">
			<head>
				{/* Preconnect to external domains for better performance */}
				<link rel="preconnect" href="https://fonts.googleapis.com" />
				<link rel="preconnect" href="https://fonts.gstatic.com" crossOrigin="anonymous" />
			</head>
			<body suppressHydrationWarning className="antialiased">
				<NextIntlClientProvider messages={messages} locale={locale}>
					{children}
				</NextIntlClientProvider>
			</body>
		</html>
	);
}

