currentFile.content:
"use client";
import Link from "next/link";
import Image from "next/image";
import { useRef, useEffect, useState } from "react";
import { useTranslations } from "next-intl";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import {
	whatwedoImg1,
	whatwedoImg2,
	whatwedoImg3,
	whatwedoImg4,
} from "@/public";

// Register ScrollTrigger plugin
if (typeof window !== "undefined") {
	gsap.registerPlugin(ScrollTrigger);
}

interface CardData {
	id: number;
	title: string;
	heading1: string;
	heading2: string;
	para: string;
	href: string;
	bgColor: string;
	textColor: string;
	linkColor: string;
	image: any;
}

export default function Card() {
	const containerRef = useRef<HTMLDivElement>(null);
	const cardsRef = useRef<HTMLDivElement[]>([]);
	const [currentCard, setCurrentCard] = useState(0);
	const [isAutoPlaying, setIsAutoPlaying] = useState(true);
	const t = useTranslations("whatwedoContent");

	const cardData: CardData[] = [
		{
			id: 1,
			title: t("title1"),
			heading1: t("heading1"),
			heading2: t("heading2"),
			para: t("para1"),
			href: t("href1"),
			bgColor: t("bgColor1"),
			textColor: t("textColor1"),
			linkColor: t("linkColor1"),
			image: whatwedoImg1,
		},
		{
			id: 2,
			title: t("title2"),
			heading1: t("heading3"),
			heading2: t("heading4"),
			para: t("para2"),
			href: t("href2"),
			bgColor: t("bgColor2"),
			textColor: t("textColor2"),
			linkColor: t("linkColor2"),
			image: whatwedoImg2,
		},
		{
			id: 3,
			title: t("title3"),
			heading1: t("heading5"),
			heading2: t("heading6"),
			para: t("para3"),
			href: t("href3"),
			bgColor: t("bgColor3"),
			textColor: t("textColor3"),
			linkColor: t("linkColor3"),
			image: whatwedoImg3,
		},
		{
			id: 4,
			title: t("title4"),
			heading1: t("heading7"),
			heading2: t("heading8"),
			para: t("para4"),
			href: t("href4"),
			bgColor: t("bgColor4"),
			textColor: t("textColor4"),
			linkColor: t("linkColor4"),
			image: whatwedoImg4,
		},
	];

	useEffect(() => {
		if (!containerRef.current) return;

		// Initial setup - hide all cards except the first one
		cardsRef.current.forEach((card, index) => {
			if (card) {
				gsap.set(card, {
					opacity: index === 0 ? 1 : 0,
					scale: index === 0 ? 1 : 0.8,
					zIndex: index === 0 ? 10 : 1,
				});
			}
		});

		// Auto-play functionality
		let autoPlayInterval: NodeJS.Timeout;

		if (isAutoPlaying) {
			autoPlayInterval = setInterval(() => {
				setCurrentCard((prev) => (prev + 1) % cardData.length);
			}, 4000);
		}

		return () => {
			if (autoPlayInterval) clearInterval(autoPlayInterval);
		};
	}, [isAutoPlaying, cardData.length]);

	useEffect(() => {
		// Animate card transitions
		cardsRef.current.forEach((card, index) => {
			if (card) {
				if (index === currentCard) {
					gsap.to(card, {
						opacity: 1,
						scale: 1,
						zIndex: 10,
						duration: 0.8,
						ease: "power2.out",
					});
				} else {
					gsap.to(card, {
						opacity: 0,
						scale: 0.8,
						zIndex: 1,
						duration: 0.6,
						ease: "power2.in",
					});
				}
			}
		});
	}, [currentCard]);

	const handleCardClick = (index: number) => {
		setCurrentCard(index);
		setIsAutoPlaying(false);
		// Resume auto-play after 10 seconds of inactivity
		setTimeout(() => setIsAutoPlaying(true), 10000);
	};

	const handlePrevCard = () => {
		setCurrentCard((prev) => (prev - 1 + cardData.length) % cardData.length);
		setIsAutoPlaying(false);
		setTimeout(() => setIsAutoPlaying(true), 10000);
	};

	const handleNextCard = () => {
		setCurrentCard((prev) => (prev + 1) % cardData.length);
		setIsAutoPlaying(false);
		setTimeout(() => setIsAutoPlaying(true), 10000);
	};

	return (
		<div className="w-full h-screen relative overflow-hidden" ref={containerRef}>
			{/* Cards Container */}
			<div className="absolute inset-0 w-full h-full">
				{cardData.map((card, index) => (
					<div
						key={card.id}
						ref={(el) => {
							if (el) cardsRef.current[index] = el;
						}}
						className="absolute inset-0 w-full h-full flex items-center justify-center p-4 xm:p-2 sm:p-3"
						style={{ backgroundColor: card.bgColor }}>
						<div className="w-full max-w-7xl h-full flex items-center justify-between gap-8 xm:flex-col sm:flex-col xm:gap-4 sm:gap-6">
							{/* Content Section */}
							<div className="w-1/2 xm:w-full sm:w-full h-full flex flex-col justify-center gap-8 xm:gap-4 sm:gap-6 px-8 xm:px-4 sm:px-6">
								<div className="flex flex-col gap-4 xm:gap-2 sm:gap-3">
									<h4
										className="text-2xl xm:text-lg sm:text-xl leading-tight tracking-tighter opacity-80"
										style={{ color: card.textColor }}>
										{card.title}
									</h4>
									<h2
										className="text-7xl xm:text-4xl sm:text-5xl md:text-6xl font-bold leading-tight tracking-tighter"
										style={{ color: card.textColor }}>
										{card.heading1}
										<br />
										{card.heading2}
									</h2>
									<p
										className="text-xl xm:text-base sm:text-lg leading-relaxed tracking-tight max-w-lg"
										style={{ color: card.textColor }}>
										{card.para}
									</p>
								</div>
								<div className="w-fit flex flex-col gap-2">
									<Link
										style={{ color: card.linkColor }}
										className="text-lg font-medium leading-tight tracking-tight hover:opacity-80 transition-opacity duration-300"
										href="/">
										{card.href}
									</Link>
									<div
										className="w-full h-[2px] rounded-lg transition-all duration-300"
										style={{ backgroundColor: card.linkColor }}
									/>
								</div>
							</div>

							{/* Image Section */}
							<div className="w-1/2 xm:w-full sm:w-full h-full flex items-center justify-center p-8 xm:p-4 sm:p-6">
								<div className="relative w-full max-w-md h-auto">
									<Image
										src={card.image}
										alt={`Service ${card.id}`}
										className="w-full h-auto object-contain rounded-2xl shadow-2xl"
										priority={index === 0}
									/>
								</div>
							</div>
						</div>
					</div>
				))}
			</div>

			{/* Navigation Controls */}
			<div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 flex items-center gap-6 z-20">
				{/* Previous Button */}
				<button
					onClick={handlePrevCard}
					className="w-12 h-12 xm:w-10 xm:h-10 rounded-full bg-white/20 backdrop-blur-sm border border-white/30 flex items-center justify-center hover:bg-white/30 transition-all duration-300"
					aria-label="Previous card">
					<svg
						className="w-6 h-6 xm:w-5 xm:h-5 text-white"
						fill="none"
						stroke="currentColor"
						viewBox="0 0 24 24">
						<path
							strokeLinecap="round"
							strokeLinejoin="round"
							strokeWidth={2}
							d="M15 19l-7-7 7-7"
						/>
					</svg>
				</button>

				{/* Dots Indicator */}
				<div className="flex gap-3">
					{cardData.map((_, index) => (
						<button
							key={index}
							onClick={() => handleCardClick(index)}
							className={`w-3 h-3 xm:w-2 xm:h-2 rounded-full transition-all duration-300 ${
								index === currentCard
									? "bg-white scale-125"
									: "bg-white/50 hover:bg-white/70"
							}`}
							aria-label={`Go to card ${index + 1}`}
						/>
					))}
				</div>

				{/* Next Button */}
				<button
					onClick={handleNextCard}
					className="w-12 h-12 xm:w-10 xm:h-10 rounded-full bg-white/20 backdrop-blur-sm border border-white/30 flex items-center justify-center hover:bg-white/30 transition-all duration-300"
					aria-label="Next card">
					<svg
						className="w-6 h-6 xm:w-5 xm:h-5 text-white"
						fill="none"
						stroke="currentColor"
						viewBox="0 0 24 24">
						<path
							strokeLinecap="round"
							strokeLinejoin="round"
							strokeWidth={2}
							d="M9 5l7 7-7 7"
						/>
					</svg>
				</button>
			</div>

			{/* Progress Bar */}
			<div className="absolute bottom-0 left-0 w-full h-1 bg-white/20 z-20">
				<div
					className="h-full bg-white transition-all duration-300 ease-linear"
					style={{
						width: `${((currentCard + 1) / cardData.length) * 100}%`,
					}}
				/>
			</div>
		</div>
	);
}

