"use client";
import Link from "next/link";
import Image from "next/image";
import { useRef, useEffect, useState } from "react";
import { useTranslations } from "next-intl";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import {
	whatwedoImg1,
	whatwedoImg2,
	whatwedoImg3,
	whatwedoImg4,
} from "@/public";

// Register ScrollTrigger plugin
if (typeof window !== "undefined") {
	gsap.registerPlugin(ScrollTrigger);
}

interface CardData {
	id: number;
	title: string;
	heading1: string;
	heading2: string;
	para: string;
	href: string;
	bgColor: string;
	textColor: string;
	linkColor: string;
	image: any;
}

export default function Card() {
	const containerRef = useRef<HTMLDivElement>(null);
	const cardsRef = useRef<HTMLDivElement[]>([]);
	const [currentCard, setCurrentCard] = useState(0);
	const [isAutoPlaying, setIsAutoPlaying] = useState(true);
	const [touchStart, setTouchStart] = useState<number | null>(null);
	const [touchEnd, setTouchEnd] = useState<number | null>(null);
	const [isLoaded, setIsLoaded] = useState(false);
	const t = useTranslations("whatwedoContent");

	const cardData: CardData[] = [
		{
			id: 1,
			title: t("title1"),
			heading1: t("heading1"),
			heading2: t("heading2"),
			para: t("para1"),
			href: t("href1"),
			bgColor: t("bgColor1"),
			textColor: t("textColor1"),
			linkColor: t("linkColor1"),
			image: whatwedoImg1,
		},
		{
			id: 2,
			title: t("title2"),
			heading1: t("heading3"),
			heading2: t("heading4"),
			para: t("para2"),
			href: t("href2"),
			bgColor: t("bgColor2"),
			textColor: t("textColor2"),
			linkColor: t("linkColor2"),
			image: whatwedoImg2,
		},
		{
			id: 3,
			title: t("title3"),
			heading1: t("heading5"),
			heading2: t("heading6"),
			para: t("para3"),
			href: t("href3"),
			bgColor: t("bgColor3"),
			textColor: t("textColor3"),
			linkColor: t("linkColor3"),
			image: whatwedoImg3,
		},
		{
			id: 4,
			title: t("title4"),
			heading1: t("heading7"),
			heading2: t("heading8"),
			para: t("para4"),
			href: t("href4"),
			bgColor: t("bgColor4"),
			textColor: t("textColor4"),
			linkColor: t("linkColor4"),
			image: whatwedoImg4,
		},
	];

	useEffect(() => {
		if (!containerRef.current) return;

		// Initial setup - hide all cards except the first one
		cardsRef.current.forEach((card, index) => {
			if (card) {
				gsap.set(card, {
					opacity: index === 0 ? 1 : 0,
					scale: index === 0 ? 1 : 0.8,
					zIndex: index === 0 ? 10 : 1,
				});
			}
		});

		// Auto-play functionality
		let autoPlayInterval: NodeJS.Timeout;

		if (isAutoPlaying) {
			autoPlayInterval = setInterval(() => {
				setCurrentCard((prev) => (prev + 1) % cardData.length);
			}, 4000);
		}

		// Keyboard navigation
		const handleKeyDown = (e: KeyboardEvent) => {
			if (e.key === 'ArrowLeft') {
				handlePrevCard();
			} else if (e.key === 'ArrowRight') {
				handleNextCard();
			}
		};

		window.addEventListener('keydown', handleKeyDown);

		// Set loaded state after initial setup
		setTimeout(() => setIsLoaded(true), 100);

		return () => {
			if (autoPlayInterval) clearInterval(autoPlayInterval);
			window.removeEventListener('keydown', handleKeyDown);
		};
	}, [isAutoPlaying, cardData.length]);

	useEffect(() => {
		// Animate card transitions
		cardsRef.current.forEach((card, index) => {
			if (card) {
				if (index === currentCard) {
					gsap.to(card, {
						opacity: 1,
						scale: 1,
						zIndex: 10,
						duration: 0.8,
						ease: "power2.out",
					});
				} else {
					gsap.to(card, {
						opacity: 0,
						scale: 0.8,
						zIndex: 1,
						duration: 0.6,
						ease: "power2.in",
					});
				}
			}
		});
	}, [currentCard]);

	const handleCardClick = (index: number) => {
		setCurrentCard(index);
		setIsAutoPlaying(false);
		// Resume auto-play after 10 seconds of inactivity
		setTimeout(() => setIsAutoPlaying(true), 10000);
	};

	const handlePrevCard = () => {
		setCurrentCard((prev) => (prev - 1 + cardData.length) % cardData.length);
		setIsAutoPlaying(false);
		setTimeout(() => setIsAutoPlaying(true), 10000);
	};

	const handleNextCard = () => {
		setCurrentCard((prev) => (prev + 1) % cardData.length);
		setIsAutoPlaying(false);
		setTimeout(() => setIsAutoPlaying(true), 10000);
	};

	// Touch handlers for swipe functionality
	const handleTouchStart = (e: React.TouchEvent) => {
		setTouchEnd(null);
		setTouchStart(e.targetTouches[0].clientX);
	};

	const handleTouchMove = (e: React.TouchEvent) => {
		setTouchEnd(e.targetTouches[0].clientX);
	};

	const handleTouchEnd = () => {
		if (!touchStart || !touchEnd) return;

		const distance = touchStart - touchEnd;
		const isLeftSwipe = distance > 50;
		const isRightSwipe = distance < -50;

		if (isLeftSwipe) {
			handleNextCard();
		} else if (isRightSwipe) {
			handlePrevCard();
		}
	};

	return (
		<div 
			className="w-full h-screen xm:h-[100dvh] sm:h-[100dvh] relative overflow-hidden select-none" 
			ref={containerRef}
			onTouchStart={handleTouchStart}
			onTouchMove={handleTouchMove}
			onTouchEnd={handleTouchEnd}>
			{/* Cards Container */}
			<div className="absolute inset-0 w-full h-full">
				{cardData.map((card, index) => (
					<div
						key={card.id}
						ref={(el) => {
							if (el) cardsRef.current[index] = el;
						}}
						className="absolute inset-0 w-full h-full flex items-center justify-center p-6 xm:p-3 sm:p-4"
						style={{ backgroundColor: card.bgColor }}>
						<div className="w-full max-w-7xl h-full flex items-center justify-between gap-8 xm:flex-col xm:justify-start xm:pt-8 sm:flex-col sm:justify-start sm:pt-6 xm:gap-6 sm:gap-8">
							{/* Content Section */}
							<div className="w-1/2 xm:w-full sm:w-full xm:h-auto sm:h-auto h-full flex flex-col justify-center xm:justify-start sm:justify-start gap-8 xm:gap-4 sm:gap-6 px-8 xm:px-2 sm:px-4">
								<div className="flex flex-col gap-4 xm:gap-3 sm:gap-4">
									<h4
										className="text-2xl xm:text-base sm:text-lg leading-tight tracking-tighter opacity-80 xm:text-center sm:text-center"
										style={{ color: card.textColor }}>
										{card.title}
									</h4>
									<h2
										className="text-7xl xm:text-3xl sm:text-4xl md:text-5xl lg:text-6xl font-bold leading-tight xm:leading-none sm:leading-tight tracking-tighter xm:text-center sm:text-center"
										style={{ color: card.textColor }}>
										{card.heading1}
										<br />
										{card.heading2}
									</h2>
									<p
										className="text-xl xm:text-sm sm:text-base leading-relaxed xm:leading-normal sm:leading-relaxed tracking-tight max-w-lg xm:max-w-none sm:max-w-none xm:text-center sm:text-center"
										style={{ color: card.textColor }}>
										{card.para}
									</p>
								</div>
								<div className="w-fit xm:w-full sm:w-full flex flex-col gap-2 xm:items-center sm:items-center">
									<Link
										style={{ color: card.linkColor }}
										className="text-lg xm:text-base sm:text-lg font-medium leading-tight tracking-tight hover:opacity-80 transition-opacity duration-300"
										href="/">
										{card.href}
									</Link>
									<div
										className="w-full h-[2px] rounded-lg transition-all duration-300 xm:max-w-[200px] sm:max-w-[250px]"
										style={{ backgroundColor: card.linkColor }}
									/>
								</div>
							</div>

							{/* Image Section */}
							<div className="w-1/2 xm:w-full sm:w-full xm:h-auto sm:h-auto h-full flex items-center justify-center p-8 xm:p-2 sm:p-4 xm:pb-20 sm:pb-16">
								<div className="relative w-full max-w-md xm:max-w-xs sm:max-w-sm h-auto">
									<Image
										src={card.image}
										alt={`Service ${card.id}`}
										className="w-full h-auto object-contain rounded-2xl xm:rounded-xl sm:rounded-xl shadow-2xl xm:shadow-lg sm:shadow-xl"
										priority={index === 0}
									/>
								</div>
							</div>
						</div>
					</div>
				))}
			</div>

			{/* Navigation Controls */}
			<div className="absolute bottom-8 xm:bottom-4 sm:bottom-6 left-1/2 transform -translate-x-1/2 flex items-center gap-6 xm:gap-3 sm:gap-4 z-20">
				{/* Previous Button */}
				<button
					onClick={handlePrevCard}
					className="w-12 h-12 xm:w-8 xm:h-8 sm:w-10 sm:h-10 rounded-full bg-white/20 backdrop-blur-sm border border-white/30 flex items-center justify-center hover:bg-white/30 transition-all duration-300 active:scale-95"
					aria-label="Previous card">
					<svg
						className="w-6 h-6 xm:w-4 xm:h-4 sm:w-5 sm:h-5 text-white"
						fill="none"
						stroke="currentColor"
						viewBox="0 0 24 24">
						<path
							strokeLinecap="round"
							strokeLinejoin="round"
							strokeWidth={2}
							d="M15 19l-7-7 7-7"
						/>
					</svg>
				</button>

				{/* Dots Indicator */}
				<div className="flex gap-3 xm:gap-2 sm:gap-2">
					{cardData.map((_, index) => (
						<button
							key={index}
							onClick={() => handleCardClick(index)}
							className={`w-3 h-3 xm:w-2 xm:h-2 sm:w-2.5 sm:h-2.5 rounded-full transition-all duration-300 active:scale-110 ${
								index === currentCard
									? "bg-white scale-125 xm:scale-150 sm:scale-125"
									: "bg-white/50 hover:bg-white/70"
							}`}
							aria-label={`Go to card ${index + 1}`}
						/>
					))}
				</div>

				{/* Next Button */}
				<button
					onClick={handleNextCard}
					className="w-12 h-12 xm:w-8 xm:h-8 sm:w-10 sm:h-10 rounded-full bg-white/20 backdrop-blur-sm border border-white/30 flex items-center justify-center hover:bg-white/30 transition-all duration-300 active:scale-95"
					aria-label="Next card">
					<svg
						className="w-6 h-6 xm:w-4 xm:h-4 sm:w-5 sm:h-5 text-white"
						fill="none"
						stroke="currentColor"
						viewBox="0 0 24 24">
						<path
							strokeLinecap="round"
							strokeLinejoin="round"
							strokeWidth={2}
							d="M9 5l7 7-7 7"
						/>
					</svg>
				</button>
			</div>

			{/* Progress Bar */}
			<div className="absolute bottom-0 left-0 w-full h-1 xm:h-0.5 sm:h-0.5 bg-white/20 z-20">
				<div
					className="h-full bg-white transition-all duration-300 ease-linear"
					style={{
						width: `${((currentCard + 1) / cardData.length) * 100}%`,
					}}
				/>
			</div>

			{/* Mobile Swipe Indicator - Only visible on small devices and fades out */}
			<div className={`absolute top-4 right-4 xm:block sm:block hidden transition-opacity duration-1000 ${isLoaded && currentCard === 0 ? 'opacity-100' : 'opacity-0'}`}>
				<div className="flex items-center gap-2 bg-white/10 backdrop-blur-sm rounded-full px-3 py-1 animate-pulse">
					<svg
						className="w-3 h-3 text-white/70"
						fill="none"
						stroke="currentColor"
						viewBox="0 0 24 24">
						<path
							strokeLinecap="round"
							strokeLinejoin="round"
							strokeWidth={2}
							d="M7 16l-4-4m0 0l4-4m-4 4h18"
						/>
					</svg>
					<span className="text-xs text-white/70 font-medium">Swipe</span>
					<svg
						className="w-3 h-3 text-white/70"
						fill="none"
						stroke="currentColor"
						viewBox="0 0 24 24">
						<path
							strokeLinecap="round"
							strokeLinejoin="round"
							strokeWidth={2}
							d="M17 8l4 4m0 0l-4 4m4-4H3"
						/>
					</svg>
				</div>
			</div>

			{/* Card Counter - Mobile */}
			<div className="absolute top-4 left-4 xm:block sm:block hidden">
				<div className="bg-white/10 backdrop-blur-sm rounded-full px-3 py-1">
					<span className="text-xs text-white/70 font-medium">
						{currentCard + 1} / {cardData.length}
					</span>
				</div>
			</div>
		</div>
	);
}

