"use client";
import Link from "next/link";
import Image from "next/image";
import { useRef, useEffect, useState } from "react";
import { useTranslations } from "next-intl";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import {
	whatwedoImg1,
	whatwedoImg2,
	whatwedoImg3,
	whatwedoImg4,
} from "@/public";

// Register ScrollTrigger plugin
if (typeof window !== "undefined") {
	gsap.registerPlugin(ScrollTrigger);
}

interface CardData {
	id: number;
	title: string;
	heading1: string;
	heading2: string;
	para: string;
	href: string;
	bgColor: string;
	textColor: string;
	linkColor: string;
	image: any;
}

export default function Card() {
	const containerRef = useRef<HTMLDivElement>(null);
	const scrollContainerRef = useRef<HTMLDivElement>(null);
	const cardsRef = useRef<HTMLDivElement[]>([]);
	const contentRefs = useRef<HTMLDivElement[]>([]);
	const imageRefs = useRef<HTMLDivElement[]>([]);
	const parallaxRefs = useRef<HTMLDivElement[]>([]);
	const [isLoaded, setIsLoaded] = useState(false);
	const t = useTranslations("whatwedoContent");

	const cardData: CardData[] = [
		{
			id: 1,
			title: t("title1"),
			heading1: t("heading1"),
			heading2: t("heading2"),
			para: t("para1"),
			href: t("href1"),
			bgColor: t("bgColor1"),
			textColor: t("textColor1"),
			linkColor: t("linkColor1"),
			image: whatwedoImg1,
		},
		{
			id: 2,
			title: t("title2"),
			heading1: t("heading3"),
			heading2: t("heading4"),
			para: t("para2"),
			href: t("href2"),
			bgColor: t("bgColor2"),
			textColor: t("textColor2"),
			linkColor: t("linkColor2"),
			image: whatwedoImg2,
		},
		{
			id: 3,
			title: t("title3"),
			heading1: t("heading5"),
			heading2: t("heading6"),
			para: t("para3"),
			href: t("href3"),
			bgColor: t("bgColor3"),
			textColor: t("textColor3"),
			linkColor: t("linkColor3"),
			image: whatwedoImg3,
		},
		{
			id: 4,
			title: t("title4"),
			heading1: t("heading7"),
			heading2: t("heading8"),
			para: t("para4"),
			href: t("href4"),
			bgColor: t("bgColor4"),
			textColor: t("textColor4"),
			linkColor: t("linkColor4"),
			image: whatwedoImg4,
		},
	];

	useEffect(() => {
		if (!containerRef.current || !scrollContainerRef.current) return;

		const container = containerRef.current;
		const scrollContainer = scrollContainerRef.current;

		const setupAnimation = () => {
			const viewportWidth = window.innerWidth;
			const isMobile = viewportWidth <= 768;
			const isTablet = viewportWidth <= 1024 && viewportWidth > 768;

			const totalWidth = cardData.length * viewportWidth;
			const scrollDistance = totalWidth - viewportWidth;

			// Adjust scroll distance based on device type
			let adjustedScrollDistance = scrollDistance;
			if (isMobile) {
				adjustedScrollDistance = Math.min(scrollDistance, viewportWidth * 2);
			} else if (isTablet) {
				adjustedScrollDistance = Math.min(scrollDistance, viewportWidth * 2.5);
			}

			// Calculate adjusted width for the scroll container
			const adjustedWidth = viewportWidth + adjustedScrollDistance;

			// Set up horizontal scroll container
			gsap.set(scrollContainer, {
				width: adjustedWidth, // Use adjusted width
				height: "100vh",
			});

			// Create horizontal scroll timeline with device-specific scroll distance
			const horizontalTl = gsap.timeline({
				scrollTrigger: {
					trigger: container,
					start: "top top",
					end: () => `+=${adjustedScrollDistance}`,
					scrub: 1,
					pin: true,
					anticipatePin: 1,
					invalidateOnRefresh: true,
					refreshPriority: -1,
				},
			});

			// Animate horizontal movement
			horizontalTl.to(scrollContainer, {
				x: -adjustedScrollDistance, // Use adjusted scroll distance
				ease: "none",
			});

			return horizontalTl;
		};

		const horizontalTl = setupAnimation();

		// Set up individual card animations
		cardsRef.current.forEach((card, index) => {
			if (!card) return;

			const content = contentRefs.current[index];
			const image = imageRefs.current[index];
			const parallaxElement = parallaxRefs.current[index];

			// Initial setup - hide elements
			gsap.set([content, image], {
				opacity: 0,
				y: 100,
			});

			if (content) {
				const textElements = content.querySelectorAll('h4, h2, p, a, div');
				gsap.set(textElements, {
					opacity: 0,
					y: 50,
					x: -30,
				});
			}

			if (image) {
				gsap.set(image, {
					opacity: 0,
					scale: 0.8,
					rotation: 0,
				});
			}

			// Create reveal animation for each card
			const cardTl = gsap.timeline({
				scrollTrigger: {
					trigger: card,
					start: "left 80%",
					end: "left 20%",
					scrub: 1,
					horizontal: true,
					containerAnimation: horizontalTl,
				}
			});

			// Animate content reveal
			if (content) {
				const textElements = content.querySelectorAll('h4, h2, p, a, div');
				cardTl
					.to(content, {
						opacity: 1,
						y: 0,
						duration: 0.6,
						ease: "power2.out",
					}, 0)
					.to(textElements, {
						opacity: 1,
						y: 0,
						x: 0,
						duration: 0.8,
						stagger: 0.1,
						ease: "power2.out",
					}, 0.2);
			}

			// Animate image reveal with parallax
			if (image) {
				cardTl.to(image, {
					opacity: 1,
					scale: 1,
					rotation: 0,
					duration: 1,
					ease: "back.out(1.7)",
				}, 0.4);
			}

			// Parallax effect for background elements
			if (parallaxElement) {
				gsap.to(parallaxElement, {
					x: -100,
					scrollTrigger: {
						trigger: card,
						start: "left right",
						end: "right left",
						scrub: 2,
						horizontal: true,
						containerAnimation: horizontalTl,
					}
				});
			}

			// Additional parallax for images
			if (image) {
				gsap.to(image, {
					x: 50,
					rotation: 0,
					scrollTrigger: {
						trigger: card,
						start: "left right",
						end: "right left",
						scrub: 3,
						horizontal: true,
						containerAnimation: horizontalTl,
					}
				});
			}
		});

		// Set loaded state
		setTimeout(() => setIsLoaded(true), 100);

		// Handle resize to recalculate on orientation change
		let resizeTimeout: NodeJS.Timeout;
		const handleResize = () => {
			clearTimeout(resizeTimeout);
			resizeTimeout = setTimeout(() => {
				ScrollTrigger.refresh();
			}, 250);
		};

		window.addEventListener('resize', handleResize);

		return () => {
			if (resizeTimeout) clearTimeout(resizeTimeout);
			window.removeEventListener('resize', handleResize);
			ScrollTrigger.getAll().forEach(trigger => trigger.kill());
		};
	}, [cardData.length]);

	// Scroll to specific card
	const scrollToCard = (index: number) => {
		if (!containerRef.current) return;

		const viewportWidth = window.innerWidth;
		const isMobile = viewportWidth <= 768;
		const isTablet = viewportWidth <= 1024 && viewportWidth > 768;

		const totalWidth = cardData.length * viewportWidth;
		const scrollDistance = totalWidth - viewportWidth;

		// Calculate adjusted scroll distance (same logic as in useEffect)
		let adjustedScrollDistance = scrollDistance;
		if (isMobile) {
			adjustedScrollDistance = Math.min(scrollDistance, viewportWidth * 2);
		} else if (isTablet) {
			adjustedScrollDistance = Math.min(scrollDistance, viewportWidth * 2.5);
		}

		// Calculate proportional scroll position
		const progress = index / (cardData.length - 1);
		const targetScrollY = containerRef.current.offsetTop + (adjustedScrollDistance * progress);

		gsap.to(window, {
			scrollTo: {
				y: targetScrollY,
				autoKill: false
			},
			duration: 1,
			ease: "power2.inOut"
		});
	};

	return (
		<div
			className="w-full h-screen relative overflow-hidden"
			ref={containerRef}>

			{/* Horizontal Scroll Container */}
			<div
				ref={scrollContainerRef}
				className="flex h-full">

				{cardData.map((card, index) => (
					<div
						key={card.id}
						ref={(el) => {
							if (el) cardsRef.current[index] = el;
						}}
						className="w-screen h-full flex-shrink-0 relative flex items-center justify-center"
						style={{ backgroundColor: card.bgColor }}>

						{/* Parallax Background Element */}
						<div
							ref={(el) => {
								if (el) parallaxRefs.current[index] = el;
							}}
							className="absolute inset-0 opacity-10"
							style={{
								background: `radial-gradient(circle at 30% 70%, ${card.linkColor}40 0%, transparent 50%)`
							}}
						/>

						{/* Card Content */}
						<div className="w-full max-w-7xl h-full flex items-center justify-between gap-8 px-6 xm:flex-col xm:justify-center xm:gap-6 sm:flex-col sm:justify-center sm:gap-8 relative z-10">

							{/* Content Section */}
							<div
								ref={(el) => {
									if (el) contentRefs.current[index] = el;
								}}
								className="w-1/2 xm:w-full sm:w-full flex flex-col justify-center gap-8 xm:gap-4 sm:gap-6 px-8 xm:px-4 sm:px-6 xm:text-center sm:text-center">

								<div className="flex flex-col gap-4 xm:gap-3 sm:gap-4">
									<h4
										className="text-2xl xm:text-lg sm:text-xl leading-tight tracking-tighter opacity-80"
										style={{ color: card.textColor }}>
										{card.title}
									</h4>

									<h2
										className="text-7xl xm:text-4xl sm:text-5xl md:text-6xl lg:text-7xl font-bold leading-tight tracking-tighter"
										style={{ color: card.textColor }}>
										{card.heading1}
										<br />
										{card.heading2}
									</h2>

									<p
										className="text-xl xm:text-base sm:text-lg leading-relaxed tracking-tight max-w-lg xm:max-w-none sm:max-w-none"
										style={{ color: card.textColor }}>
										{card.para}
									</p>
								</div>

								<div className="w-fit xm:w-full sm:w-full flex flex-col gap-2 xm:items-center sm:items-center">
									<Link
										key={card.id}
										style={{ color: card.linkColor }}
										className="text-lg xm:text-base sm:text-lg font-medium leading-tight tracking-tight hover:opacity-80 transition-opacity duration-300 relative group"
										href={card.href}>
										{card.href}
										<span className="absolute -bottom-1 left-0 w-0 h-0.5 bg-current transition-all duration-300 group-hover:w-full" style={{ backgroundColor: card.linkColor }}></span>
									</Link>
								</div>
							</div>

							{/* Image Section */}
							<div
								ref={(el) => {
									if (el) imageRefs.current[index] = el;
								}}
								className="w-1/2 xm:w-full sm:w-full flex items-center justify-center p-8 xm:p-4 sm:p-6">

								<div className="relative w-full max-w-md xm:max-w-sm sm:max-w-md">
									{/* Image Glow Effect */}
									<div
										className="absolute inset-0 rounded-2xl blur-2xl opacity-20"
										style={{ backgroundColor: card.linkColor }}
									/>

									<Image
										src={card.image}
										alt={card.title}
										className="relative w-full h-auto object-contain rounded-2xl shadow-2xl transition-transform duration-300 hover:scale-105"
										priority={index === 0}
									/>
								</div>
							</div>
						</div>
					</div>
				))}
			</div>

			{/* Scroll Hint */}
			<div className={`fixed bottom-20 left-1/2 transform -translate-x-1/2 transition-opacity duration-1000 z-40 xm:bottom-16 sm:bottom-18 ${isLoaded ? 'opacity-100' : 'opacity-0'}`}>
				<div className="flex items-center gap-2 text-white/70 animate-bounce">
					<svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
						<path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 14l-7 7m0 0l-7-7m7 7V3" />
					</svg>
					<span className="text-sm font-medium">Scroll to explore</span>
				</div>
			</div>
		</div>
	);
}

