"use client";
import { useEffect, useRef, useState } from "react";
import { useTranslations } from "next-intl";
import Image from "next/image";
import gsap from "gsap";

import {
    whatwedoImg1,
    whatwedoImg2,
    whatwedoImg3,
    whatwedoImg4,
} from "@/public";

if (typeof window !== "undefined") {
    gsap.registerPlugin();
}

const getCards = (t: (key: string) => string) => [
    {
        bg: t("bgColor1"),
        text: t("textColor1"),
        link: t("linkColor1"),
        heading: [t("heading1"), t("heading2")],
        para: t("para1"),
        img: whatwedoImg1,
    },
    {
        bg: t("bgColor2"),
        text: t("textColor2"),
        link: t("linkColor2"),
        heading: [t("heading3"), t("heading4")],
        para: t("para2"),
        img: whatwedoImg2,
    },
    {
        bg: t("bgColor3"),
        text: t("textColor3"),
        link: t("linkColor3"),
        heading: [t("heading5"), t("heading6")],
        para: t("para3"),
        img: whatwedoImg3,
    },
    {
        bg: t("bgColor4"),
        text: t("textColor4"),
        link: t("linkColor4"),
        heading: [t("heading7"), t("heading8")],
        para: t("para4"),
        img: whatwedoImg4,
    },
];

export default function CardV2() {
    const t = useTranslations("aboutUsContent");
    const cards = getCards(t);
    const containerRef = useRef<HTMLDivElement>(null);
    const cardRefs = useRef<(HTMLDivElement | null)[]>([]);
    const [isGrid, setIsGrid] = useState(false);

    // Configs
    const cardWidth = 300;
    const cardHeight = 380;
    const gap = 24;
    const morphDuration = 1.2;
    const gridPause = 4000;
    const carouselDuration = 40;

    useEffect(() => {
        if (!containerRef.current) return;
        const container = containerRef.current;
        const cardsCount = cards.length;

        // Duplicate cards for infinite loop
        container.innerHTML += container.innerHTML;

        const allCards = container.querySelectorAll(".card-item");

        // Setup initial horizontal layout (carousel)
        gsap.set(container, {
            display: "flex",
            flexWrap: "nowrap",
            overflow: "visible",
            width: "max-content",
        });

        allCards.forEach((card, i) => {
            gsap.set(card, {
                position: "relative",
                width: cardWidth,
                height: cardHeight,
                marginRight: gap,
                flexShrink: 0,
                borderRadius: 24,
                overflow: "hidden",
                boxShadow:
                    "0 10px 20px rgba(0,0,0,0.12), 0 6px 6px rgba(0,0,0,0.08)",
            });
        });

        // Timeline for infinite scroll
        const scrollDistance = (cardWidth + gap) * cardsCount;
        const infiniteScroll = gsap.timeline({ repeat: -1, defaults: { ease: "linear" } });
        infiniteScroll.to(container, {
            x: `-=${scrollDistance}px`,
            duration: carouselDuration,
            modifiers: {
                x: (x) => {
                    const mod = parseFloat(x) % scrollDistance;
                    return `${mod}px`;
                },
            },
        });

        // Morph between carousel and grid every (carouselDuration + gridPause)
        const masterTimeline = gsap.timeline({ repeat: -1, repeatDelay: 0 });

        // Helper: grid positions (2x2)
        const gridPositions = [
            { x: 0, y: 0 },
            { x: cardWidth + gap, y: 0 },
            { x: 0, y: cardHeight + gap },
            { x: cardWidth + gap, y: cardHeight + gap },
        ];

        masterTimeline.add(infiniteScroll);

        masterTimeline.to(container, {
            duration: morphDuration,
            x: 0,
            ease: "power2.inOut",
            onStart: () => setIsGrid(true),
        });

        masterTimeline.to(allCards, {
            duration: morphDuration,
            ease: "power2.inOut",
            x: (i) => gridPositions[i % cardsCount].x,
            y: (i) => gridPositions[i % cardsCount].y,
            marginRight: 0,
            marginBottom: gap,
            scale: 1,
            stagger: 0.1,
        }, `-=${morphDuration}`);

        masterTimeline.to({}, { duration: gridPause }); // pause on grid

        masterTimeline.to(allCards, {
            duration: morphDuration,
            ease: "power2.inOut",
            x: 0,
            y: 0,
            marginRight: gap,
            marginBottom: 0,
            stagger: 0.1,
        });

        masterTimeline.to(container, {
            duration: morphDuration,
            ease: "power2.inOut",
            x: 0,
            onComplete: () => setIsGrid(false),
        });

        // Cleanup
        return () => {
            masterTimeline.kill();
            infiniteScroll.kill();
        };
    }, [cards]);

    return (
        <div className="relative w-full flex justify-center py-6 overflow-hidden">
            <div
                ref={containerRef}
                className="select-none cursor-grab"
                style={{
                    userSelect: "none",
                    touchAction: "pan-y",
                    position: "relative",
                    width: "max-content",
                    willChange: "transform",
                }}
            >
                {cards.map((card, idx) => (
                    <div
                        key={idx}
                        ref={(el) => {
                            cardRefs.current[idx] = el;
                        }}
                        className="card-item text-[#0D1B2A] p-6"
                        style={{
                            backgroundColor: card.bg,
                            color: card.text,
                            boxShadow:
                                "0 6px 15px rgba(0,0,0,0.1)",
                            borderRadius: 24,
                            width: cardWidth,
                            height: cardHeight,
                            marginRight: gap,
                            display: "flex",
                            flexDirection: "column",
                        }}
                    >
                        <div className="relative flex-shrink-0 h-44 rounded-xl overflow-hidden mb-4">
                            <Image
                                src={card.img}
                                alt="card image"
                                fill
                                style={{ objectFit: "cover", borderRadius: 24 }}
                                priority
                            />
                        </div>
                        <h2 className="text-2xl font-bold mb-2 leading-snug tracking-tight">
                            {card.heading.map((h, i) => (
                                <span key={i} className="block">
                  {h}
                </span>
                            ))}
                        </h2>
                        <p className="text-base flex-grow">{card.para}</p>
                        <div
                            className="mt-4 h-1 rounded-full"
                            style={{ backgroundColor: card.link }}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
}

