"use client";
import { useCallback, useEffect, useMemo, useRef, useState } from "react";
import { useTranslations } from "next-intl";
import Image from "next/image";
import gsap from "gsap";

import {
    whatwedoImg1,
    whatwedoImg2,
    whatwedoImg3,
    whatwedoImg4,
} from "@/public";

if (typeof window !== "undefined") {
    gsap.registerPlugin();
}

// Animation configuration constants
const ANIMATION_CONFIG = {
    cardWidth: 300,
    cardHeight: 380,
    gap: 24,
    morphDuration: 1.2,
    gridPause: 4000,
    carouselDuration: 40,
} as const;

const cardImages = [whatwedoImg1, whatwedoImg2, whatwedoImg3, whatwedoImg4] as const;

export default function CardV2() {
    const t = useTranslations("aboutUsContent");

    // Memoize cards data to prevent unnecessary recalculations
    const cards = useMemo(() => [
        {
            bg: t("bgColor1"),
            text: t("textColor1"),
            link: t("linkColor1"),
            heading: [t("heading1"), t("heading2")],
            para: t("para1"),
            img: cardImages[0],
        },
        {
            bg: t("bgColor2"),
            text: t("textColor2"),
            link: t("linkColor2"),
            heading: [t("heading3"), t("heading4")],
            para: t("para2"),
            img: cardImages[1],
        },
        {
            bg: t("bgColor3"),
            text: t("textColor3"),
            link: t("linkColor3"),
            heading: [t("heading5"), t("heading6")],
            para: t("para3"),
            img: cardImages[2],
        },
        {
            bg: t("bgColor4"),
            text: t("textColor4"),
            link: t("linkColor4"),
            heading: [t("heading7"), t("heading8")],
            para: t("para4"),
            img: cardImages[3],
        },
    ], [t]);

    const containerRef = useRef<HTMLDivElement>(null);
    const cardRefs = useRef<(HTMLDivElement | null)[]>([]);
    const [isGrid, setIsGrid] = useState(false);
    const timelinesRef = useRef<{ master?: gsap.core.Timeline; infinite?: gsap.core.Timeline }>({});

    // Destructure config for better readability
    const { cardWidth, cardHeight, gap, morphDuration, gridPause, carouselDuration } = ANIMATION_CONFIG;

    // Memoize grid positions calculation
    const gridPositions = useMemo(() => [
        { x: 0, y: 0 },
        { x: cardWidth + gap, y: 0 },
        { x: 0, y: cardHeight + gap },
        { x: cardWidth + gap, y: cardHeight + gap },
    ], [cardWidth, cardHeight, gap]);

    // Memoize scroll distance calculation
    const scrollDistance = useMemo(() => (cardWidth + gap) * cards.length, [cardWidth, gap, cards.length]);

    // Setup animations with proper cleanup
    useEffect(() => {
        if (!containerRef.current) return;

        const container = containerRef.current;
        const cardsCount = cards.length;
        const allCards = container.querySelectorAll(".card-item");

        // Clean up previous animations
        if (timelinesRef.current.master) {
            timelinesRef.current.master.kill();
        }
        if (timelinesRef.current.infinite) {
            timelinesRef.current.infinite.kill();
        }

        // Setup initial horizontal layout (carousel)
        gsap.set(container, {
            display: "flex",
            flexWrap: "nowrap",
            overflow: "visible",
            width: "max-content",
        });

        // Setup card styles with better performance
        gsap.set(allCards, {
            position: "relative",
            width: cardWidth,
            height: cardHeight,
            marginRight: gap,
            flexShrink: 0,
            borderRadius: 24,
            overflow: "hidden",
            force3D: true, // Enable hardware acceleration
            willChange: "transform",
        });

        // Timeline for infinite scroll
        const infiniteScroll = gsap.timeline({ 
            repeat: -1, 
            defaults: { ease: "none" }, // Use "none" for better performance on infinite animations
            paused: true 
        });

        infiniteScroll.to(container, {
            x: `-=${scrollDistance}px`,
            duration: carouselDuration,
            modifiers: {
                x: (x) => {
                    const mod = parseFloat(x) % scrollDistance;
                    return `${mod}px`;
                },
            },
        });

        // Master timeline for morphing
        const masterTimeline = gsap.timeline({ repeat: -1, repeatDelay: 0 });

        // Store references for cleanup
        timelinesRef.current.infinite = infiniteScroll;
        timelinesRef.current.master = masterTimeline;

        // Start with carousel
        infiniteScroll.play();
        masterTimeline.add(infiniteScroll, 0);

        // Morph to grid
        masterTimeline.to(container, {
            duration: morphDuration,
            x: 0,
            ease: "power2.inOut",
            onStart: () => setIsGrid(true),
        });

        masterTimeline.to(allCards, {
            duration: morphDuration,
            ease: "power2.inOut",
            x: (i) => gridPositions[i % cardsCount].x,
            y: (i) => gridPositions[i % cardsCount].y,
            marginRight: 0,
            marginBottom: gap,
            scale: 1,
            stagger: 0.1,
            force3D: true,
        }, `-=${morphDuration}`);

        // Pause on grid
        masterTimeline.to({}, { duration: gridPause / 1000 }); // Convert to seconds

        // Morph back to carousel
        masterTimeline.to(allCards, {
            duration: morphDuration,
            ease: "power2.inOut",
            x: 0,
            y: 0,
            marginRight: gap,
            marginBottom: 0,
            stagger: 0.1,
            force3D: true,
        });

        masterTimeline.to(container, {
            duration: morphDuration,
            ease: "power2.inOut",
            x: 0,
            onComplete: () => setIsGrid(false),
        });

        // Cleanup function
        return () => {
            if (timelinesRef.current.master) {
                timelinesRef.current.master.kill();
                timelinesRef.current.master = undefined;
            }
            if (timelinesRef.current.infinite) {
                timelinesRef.current.infinite.kill();
                timelinesRef.current.infinite = undefined;
            }
        };
    }, [cards.length, scrollDistance, gridPositions, cardWidth, cardHeight, gap, morphDuration, gridPause, carouselDuration]);

    // Memoize card component to prevent unnecessary re-renders
    const CardComponent = useCallback(({ card, index, isDuplicate = false }: { 
        card: typeof cards[0]; 
        index: number; 
        isDuplicate?: boolean; 
    }) => (
        <div
            key={isDuplicate ? `duplicate-${index}` : index}
            ref={(el) => {
                if (!isDuplicate) {
                    cardRefs.current[index] = el;
                }
            }}
            className="card-item text-[#0D1B2A] p-6"
            style={{
                backgroundColor: card.bg,
                color: card.text,
                boxShadow: "0 6px 15px rgba(0,0,0,0.1)",
                borderRadius: 24,
                width: cardWidth,
                height: cardHeight,
                marginRight: gap,
                display: "flex",
                flexDirection: "column",
                flexShrink: 0,
                willChange: "transform",
            }}
        >
            <div className="relative flex-shrink-0 h-44 rounded-xl overflow-hidden mb-4">
                <Image
                    src={card.img}
                    alt={`Card ${index + 1} image`}
                    fill
                    style={{ objectFit: "cover", borderRadius: 12 }}
                    priority={index < 2} // Only prioritize first 2 images
                    sizes="300px"
                />
            </div>
            <h2 className="text-2xl font-bold mb-2 leading-snug tracking-tight">
                {card.heading.map((h, i) => (
                    <span key={i} className="block">
                        {h}
                    </span>
                ))}
            </h2>
            <p className="text-base flex-grow">{card.para}</p>
            <div
                className="mt-4 h-1 rounded-full"
                style={{ backgroundColor: card.link }}
            />
        </div>
    ), [cardWidth, cardHeight, gap]);

    // Memoize duplicated cards array
    const allCards = useMemo(() => [
        ...cards.map((card, index) => ({ card, index, isDuplicate: false })),
        ...cards.map((card, index) => ({ card, index, isDuplicate: true })),
    ], [cards]);

    return (
        <div className="relative w-full flex justify-center py-6 overflow-hidden">
            <div
                ref={containerRef}
                className="select-none cursor-grab"
                style={{
                    userSelect: "none",
                    touchAction: "pan-y",
                    position: "relative",
                    width: "max-content",
                    willChange: "transform",
                    display: "flex",
                    flexWrap: "nowrap",
                }}
            >
                {allCards.map(({ card, index, isDuplicate }) => (
                    <CardComponent
                        key={isDuplicate ? `duplicate-${index}` : index}
                        card={card}
                        index={index}
                        isDuplicate={isDuplicate}
                    />
                ))}
            </div>
        </div>
    );
}

